select INFORMATION_SCHEMA.COLUMNS.COLUMN_NAME as [Name], INFORMATION_SCHEMA.COLUMNS.ORDINAL_POSITION AS [Index], INFORMATION_SCHEMA.COLUMNS.DATA_TYPE as [TypeName], sys.extended_properties.value as [Description], INFORMATION_SCHEMA.COLUMNS.CHARACTER_MAXIMUM_LENGTH as [MaxLength], INFORMATION_SCHEMA.COLUMNS.COLUMN_DEFAULT as [DefaultExpression], INFORMATION_SCHEMA.COLUMNS.IS_NULLABLE as [IsNullable], COLUMNPROPERTY(OBJECT_ID('dbo.tableSqlHelperTest'), INFORMATION_SCHEMA.COLUMNS.COLUMN_NAME, 'IsIdentity') as [IsIdentity], INFORMATION_SCHEMA.TABLE_CONSTRAINTS.CONSTRAINT_TYPE AS [ConstraintType], kcu.TABLE_NAME as [ReferenceTable], kcu.COLUMN_NAME as [ReferenceField]
from INFORMATION_SCHEMA.COLUMNS
LEFT OUTER JOIN sys.extended_properties ON sys.extended_properties.class = 1 and sys.extended_properties.major_id = OBJECT_ID('dbo.tableSqlHelperTest') and sys.extended_properties.minor_id = COLUMNPROPERTY(OBJECT_ID('dbo.tableSqlHelperTest'), INFORMATION_SCHEMA.COLUMNS.COLUMN_NAME, 'ColumnId')
LEFT OUTER JOIN INFORMATION_SCHEMA.KEY_COLUMN_USAGE ON INFORMATION_SCHEMA.COLUMNS.COLUMN_NAME = INFORMATION_SCHEMA.KEY_COLUMN_USAGE.COLUMN_NAME AND INFORMATION_SCHEMA.KEY_COLUMN_USAGE.TABLE_NAME = 'tableSqlHelperTest' AND INFORMATION_SCHEMA.KEY_COLUMN_USAGE.TABLE_SCHEMA = 'dbo'
LEFT OUTER JOIN INFORMATION_SCHEMA.TABLE_CONSTRAINTS ON INFORMATION_SCHEMA.KEY_COLUMN_USAGE.CONSTRAINT_NAME = INFORMATION_SCHEMA.TABLE_CONSTRAINTS.CONSTRAINT_NAME AND INFORMATION_SCHEMA.TABLE_CONSTRAINTS.TABLE_NAME = 'tableSqlHelperTest' AND INFORMATION_SCHEMA.TABLE_CONSTRAINTS.TABLE_SCHEMA = 'dbo'
LEFT OUTER JOIN INFORMATION_SCHEMA.REFERENTIAL_CONSTRAINTS ON INFORMATION_SCHEMA.TABLE_CONSTRAINTS.CONSTRAINT_NAME = REFERENTIAL_CONSTRAINTS.CONSTRAINT_NAME
LEFT OUTER JOIN INFORMATION_SCHEMA.KEY_COLUMN_USAGE AS kcu ON INFORMATION_SCHEMA.REFERENTIAL_CONSTRAINTS.UNIQUE_CONSTRAINT_NAME = kcu.CONSTRAINT_NAME AND INFORMATION_SCHEMA.KEY_COLUMN_USAGE.ORDINAL_POSITION = kcu.ORDINAL_POSITION
where INFORMATION_SCHEMA.COLUMNS.TABLE_NAME = 'tableSqlHelperTest' AND INFORMATION_SCHEMA.COLUMNS.TABLE_SCHEMA = 'dbo'
order by INFORMATION_SCHEMA.COLUMNS.ORDINAL_POSITION asc