select INFORMATION_SCHEMA.COLUMNS.COLUMN_NAME as [Name], INFORMATION_SCHEMA.COLUMNS.ORDINAL_POSITION AS [Index], INFORMATION_SCHEMA.COLUMNS.DATA_TYPE as [TypeName], INFORMATION_SCHEMA.COLUMNS.CHARACTER_MAXIMUM_LENGTH as [MaxLength], INFORMATION_SCHEMA.COLUMNS.IS_NULLABLE as [IsNullable]
from INFORMATION_SCHEMA.COLUMNS
where INFORMATION_SCHEMA.COLUMNS.TABLE_NAME = 'viewSqlHelperTest'
order by INFORMATION_SCHEMA.COLUMNS.ORDINAL_POSITION asc